CREATE TABLE "public"."simulator_parameters" ("id" serial NOT NULL, "simulator_id" integer NOT NULL, "failure_type_id" integer NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("failure_type_id") REFERENCES "public"."failure_types"("id") ON UPDATE no action ON DELETE no action, FOREIGN KEY ("simulator_id") REFERENCES "public"."simulators"("id") ON UPDATE no action ON DELETE no action, UNIQUE ("id"));
